mapper.select:
SELECT * 
	FROM hotel_room 
	WHERE h_no = #{h_no} 
	AND room_type = #{room_type}

SELECT room_price 
	FROM type 
	WHERE h_no = #{h_no} 
	AND room_type = #{room_type}

SELECT COUNT(*) 
	FROM type 
	WHERE h_no = #{h_no} 
	AND room_type = #{room_type}

 		SELECT
   			COUNT(*)
			FROM hotel_room 
			WHERE h_no = #{h_no} 
			AND room_type=#{room_type}

SELECT room_num 
	FROM hotel_room
	WHERE room_type =#{room_type}
	AND h_no=#{h_no}

mapper.update:
UPDATE hotel_room 
	SET pic_room_url = #{pic_room_url}
	WHERE room_type = #{room_type} 
	AND h_no=#{h_no}

 			UPDATE type 
	 			SET room_price = #{room_price}
	 			WHERE room_type = #{room_type}
	 			AND h_no=#{h_no}

mapper.delete:
delete from type 
	where h_no = #{h_no}
	AND room_type =#{room_type}
